-- 
-- 表的结构 `yr_article`
-- 

CREATE TABLE `yr_article` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `cate` varchar(20) NOT NULL DEFAULT '' COMMENT '文章分类',
  `role` enum('公开','私密') NOT NULL DEFAULT '公开' COMMENT '权限',
  `author` varchar(50) NOT NULL DEFAULT '' COMMENT '作者',
  `pic` varchar(32) NOT NULL DEFAULT '' COMMENT '插图',
  `addtime` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP COMMENT '发布时间',
  `title` varchar(100) NOT NULL DEFAULT '' COMMENT '文章标题',
  `content` text COMMENT '文章内容',
  `status` enum('默认','最新','最热') NOT NULL DEFAULT '默认' COMMENT '文章状态',
  `sum` int(10) unsigned NOT NULL DEFAULT '0',
  `auditing` enum('T','F') NOT NULL DEFAULT 'F' COMMENT '审核',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=41 DEFAULT CHARSET=utf8 AUTO_INCREMENT=41 ;

