CREATE TABLE `yr_user` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `username` varchar(32) NOT NULL COMMENT '用户名',
  `password` char(32) NOT NULL COMMENT '密码',
  `email` varchar(20) NOT NULL COMMENT '邮箱',
  `token` varchar(50) NOT NULL,
  `token_exptime` int(10) NOT NULL,
  `status` int(1) NOT NULL DEFAULT '0',
  `addtime` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP COMMENT '注册时间',
  `user_ip` varchar(32) DEFAULT NULL COMMENT '用户ip',
  `user_pic` varchar(32) DEFAULT NULL,
  `realname` varchar(20) DEFAULT NULL,
  `gener` enum('男','女','保密') NOT NULL DEFAULT '保密',
  PRIMARY KEY (`id`)
) ENGINE=MyISAM AUTO_INCREMENT=26 DEFAULT CHARSET=utf8 AUTO_INCREMENT=26 ;

